//misc functions
function watermarkEnter(obj,wm){if(obj.value==wm){obj.value='';obj.style.color='';obj.select();}}
function watermarkLeave(obj,wm){if(obj.value==''){obj.value=wm;}}
function trackUserLocation(location){if(location==null){return;}}
